USE MARCIN_NIERUCHOMOSC;GOCREATE TRIGGER TR_SPRAWDZ_POPRAWNOSC_DATY_ORAZ_DOSTEPNOSC_WYNAJECIA    ON NAJEM_INDYWIDUALNY    INSTEAD OF INSERT    ASBEGIN    DECLARE @FROM date;    DECLARE @TO date;    DECLARE @NIERUCH bigint;    DECLARE @UUID varchar(255);    SELECT @UUID = inserted.UUID, @FROM = inserted.DATA_OD, @TO = inserted.DATA_DO, @NIERUCH = inserted.ID_NIERUCHOMOSC    FROM inserted;    IF (@TO < @FROM OR @FROM < GETDATE())        BEGIN            RAISERROR ('Zle daty wynajmu! Data OD jest po dacie PO lub OD znajduje sie w przeszlosci!',17,1);        END    ELSE        IF EXISTS(SELECT *                  FROM NAJEM_INDYWIDUALNY                  WHERE NAJEM_INDYWIDUALNY.ID_NIERUCHOMOSC = @NIERUCH                    AND NAJEM_INDYWIDUALNY.DATA_OD >= @FROM                    AND NAJEM_INDYWIDUALNY.DATA_DO <= @TO)            BEGIN                RAISERROR ('Nieruchomość jest wynajmowana w tym terminie!',17,1);            END        ELSE            BEGIN                INSERT INTO NAJEM_INDYWIDUALNY (UUID, ID_KLIENT_INDYWIDUALNY, DATA_OD, DATA_DO, ID_NIERUCHOMOSC,                                                WIELKOSC_RABATU, ID_RACHUNEK, ILE_DNI)                SELECT inserted.UUID,                       inserted.ID_KLIENT_INDYWIDUALNY,                       inserted.DATA_OD,                       inserted.DATA_DO,                       inserted.ID_NIERUCHOMOSC,                       dbo.FN_ILE_PROCENT_RABATU(inserted.DATA_OD, inserted.DATA_DO),                       null,                       DATEDIFF(day, inserted.DATA_OD, inserted.DATA_DO)                FROM inserted;            END    IF EXISTS(SELECT * FROM NAJEM_INDYWIDUALNY AS NI WHERE NI.UUID = @UUID)        BEGIN            EXEC dbo.PROC_DODAJ_RACHUNEK @UUID_NAJEM = @UUID;        ENDENDGOINSERT INTO NAJEM_INDYWIDUALNY(UUID, ID_KLIENT_INDYWIDUALNY, DATA_OD, DATA_DO, ID_NIERUCHOMOSC, WIELKOSC_RABATU,                               ID_RACHUNEK)VALUES (dbo.getUUID(), 1, convert(datetime, '24.12.2019', 104), convert(datetime, '29.12.2019', 104), 1, null, null);-- GO---- INSERT INTO NAJEM(UUID, ID_AGENT, DATA_OD, DATA_DO, ID_KLIENT_INDYWIDUALNY, ID_KLIENT_FIRMA, ID_NIERUCHOMOSC,--                   WIELKOSC_RABATU, ARCHIWUM)-- VALUES (dbo.getUUID(), 1, convert(datetime, '13.10.2016', 104), convert(datetime, '22.10.2016', 104), 1, null, 1, null,--         0);